import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
state ={
        "date" : "2018-01-01",
        "rates":{"USD":1.0,"GBP":10.0},
        "baseCode":"USD "        
}

  // Add data during initial page load. Kind of likke constructor
    componentDidMount = () => {
    this.getData();
  }



  render() 
  {
    return (
    <div className="App">
      <h1 className="Title"> Currency</h1>
      <h2 className="BaseValue">Base Currency:</h2>
      <select baseCode={this.baseCode} className="BaseCode" onChange={this.onBaseCodeChange}>
        <option value="USA">USA</option>
        <option value="AUD">AUD</option>
        <option value="SGD">SGD</option>
        <option value="EUR">EUR</option>
      </select>
      <div className="Graph">
        <div className="Graph-data1" style="height:88.5%">EUR &#8364</div>
        <div className="Graph-data2" style="height:75.5%">USD $</div>
        <div className="Graph-data3" style="height:55.9%">AUD $</div>
        <div className="Graph-data4" style="height:45.5%">GBP &pound</div>
      </div>
    </div>
    );
  }

  onBaseCodeChange = (ev) => {
    this.setState({
      baseCode: ev.target.value,
    });
  }

/*
  onSubmit = () => {
      // TODO fill me in
      // HINT: https://api.github.com/users/michaelpb/repos
  }

*/

getData()
{
    console.log("In GetData");

    // The data format from the remote site will be
    // "date": "YYYY-MM-DD",
    // "rates": {"CCC":dec,"CCC":dec,}, "base":"
    /* Use to test
    const data =
    {
        "date" : "2018-01-01",
        "rates":{"USD":1.0,"GBP":10.0},
        "base":"USD "        
    };
    */

    /*  Static Values for testing
    let data =  {
        "date" : "2018-01-01",
        "rates":{"USD":1.0,"GBP":10.0},
        "base":"USD "        
    };
    */

     fetch("https://api.exchangeratesapi.io/latest?base=USD&symbols=BGN,SGD,AUD,EUR")
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log(data);
           this.buildData(data.rates);
       });


}


buildData(rates)
{
    console.log("In buildData");

    //Using rates, refresh the graph

    /*
    // The format of the HTML to be modified is:
    let graph = document.querySelect('#Graph');
    <div className="Graph">
        <div className="Graph-data1" onclick="alert(\'EUR costs 0.88lb\') " style="height:88.5%" ">EUR &#8364</div>
        <div className="Graph-data2" style="height:75.5%">USD $</div>
        <div className="Graph-data3" style="height:55.9%">AUD $</div>
        <div className="Graph-data4" style="height:45.5%">GBP &pound</div>
    </div>
    */

    // Get the rates
    // Note: used for..of becasue it is MUCH faster than Object.entries
    let lineNum = 1;

    for (const key of Object.keys(this.rates))
    {
        const value = this.rates[key];

        //Check for properties from prototype chain   
        if (this.rates.hasOwnProperty(key))
        {
            //not a property from prototype chain
            let graphString='.Graph-data' + lineNum++;

            // format value to be x.xx
            const formatValue = parseFloat(value).toFixed(2) * 30;
            // format the HTML style attribute
            const style = 'height:' + formatValue + '%';

            /*
            // Modify the DOM Element
            let graphBar = document.querySelector(graphString); 
            graphBar.setAttribute("style", style);

            // Add text to bar
            const symbol = "$";   // Should automate this
            graphBar.textContent=key + " " + symbol;

            */
        }
    }
}

getSelect()
{
    // Create a query string by setting the new baseCode and removing new base from comparator
    console.log("In GetSelect");

    // Currently displayed currencies;
    let symbols = ["BGN", "SGD", "AUD", "EUR", "USD"];
    let comparator = "BGN,SGD,AUD,EUR,USD";


    // Get the requested base currency
    //const baseCode = document.getElementById("BaseCode").value;
    console.log("New Base Code:", this.baseCode);


    let i = 0;
    while ((symbols[i++].valueOf() != this.baseCode) && ( i < symbols.length))  // find the code in symbols
    {}

    i--;  // reposition counter to found item
    if (symbols[i] === this.baseCode)
    {
        comparator = symbols.splice(i,1);  // remmove elemment
    }
    console.log("symbols:", comparator);

    let fetchString='https://api.exchangeratesapi.io/2018-11-01?base=' + this.baseCode + "&symbols=" + symbols;

    // Make request
    fetch(fetchString)
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log(data);
           this.buildData(data.rates);
       });
}


}

export default App;
