    {
        comparator = symbols.splice(i,1);  // remmove elemment
    }
    console.log("symbols:", comparator);

    let fetchString='https://api.exchangeratesapi.io/2018-11-01?base=' + this.baseCode + "&symbols=" + symbols;

    // Make request
    fetch(fetchString)
