import React, { Component } from 'react';
import './App.css';


import Bar from './Components/Bar/Bar.js'
import Graph from './Components/Graph/Graph.js'

class App extends Component {

state = {
  baseCode:"USD",
  rates:[
  {
    barStyle: "height:'51.6%'", 
    countryCode: "BGN",
    monetarySymbol:"$",     
  },
  {
    barStyle: '3%', 
    countryCode: "GBP",
    monetarySymbol:"$",     
  },
  {
    barStyle: '{height:"1.4%"}', 
    countryCode: "AUD",
    monetarySymbol:"$",     
  },
  {
    barStyle: {height:'90.4%'}, 
    countryCode: "EUR",
    monetarySymbol:"$",     
  },
   {
    barStyle: "90%", 
    countryCode: "GDP",
    monetarySymbol:"$",     
  }
  ],
}

  // Add data during initial page load. Kind of likke constructor
    componentDidMount = () => {
    console.log("In did Mount and State:", this.state);
    this.getData();
  }

/*
  render() 
  {

    console.log("In render");

    return (
    <div className="App">
      <h1 className="Title"> Currency</h1>
      <h2 className="BaseValue">Base Currency:</h2>
      <select basecode={this.baseCode} className="BaseCode" onChange={this.onBaseCodeChange}>
        <option value="USA">USA</option>
        <option value="AUD">AUD</option>
        <option value="SGD">SGD</option>
        <option value="EUR">EUR</option>
      </select>
      <Graph>
        this.state.rates.map((values, indx)=>{
          <Bar
          style={idx} 
          title={idx}
          symbol={idx} 
          />
      })
      </Graph>
    </div>
    );
  }
*/

render() 
{

    return (
      <div>
      <h1 className="Title"> Currency</h1>
      <h2 className="BaseValue">Base Currency:</h2>
      <select basecode={this.baseCode} className="BaseCode" onChange={this.onBaseCodeChange}>
        <option value="USA">USA</option>
        <option value="AUD">AUD</option>
        <option value="SGD">SGD</option>
        <option value="EUR">EUR</option>
      </select>
      <Graph>      
      {this.state.rates.map(function(d,idx){
         const barStyle = d.barStyle;
         return (<Bar 
         key={idx}
         style={barStyle}
//         style= {this.getStyle()}
         title={d.countryCode} 
         symbol={d.monetarySymbol}/>)
       })}
       </Graph>
      </div>
    );
  }

  onBaseCodeChange = (ev) => {
    this.setState({
      baseCode: ev.target.value,
    });
  }

getData()
{
    console.log("In GetData");

    // The data format from the remote site will be
    // "date": "YYYY-MM-DD",
    // "rates": {"CCC":dec,"CCC":dec,}, 
    // "base":  "3-letter string"

    /* Use to test
    const data =
    {
        "date" : "2018-01-01",
        "rates":{"USD":1.0,"GBP":10.0},
        "base":"USD "        
    };


     fetch("https://api.exchangeratesapi.io/latest?base=USD&symbols=BGN,GBP,AUD,EUR")
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log("Data=", data);
           console.log("Rates=", data.rates);
           this.buildData(data.rates);
       });

/*       
       /* Not working
        // 404
        if (!data.main) {
          this.setState({
            location: "Not found.",
          });
          return;
        }
*/
}


buildData(rates)
{
    console.log("In buildData");
    console.log("Rates=", rates);

    let arrData = [];


    for (const key of Object.keys(rates))
    {
        console.log("in loop to get rates");
        const value = rates[key];
        console.log("value=",value);

        //Check for properties from prototype chain   
        if (rates.hasOwnProperty(key))
        {
            let newData = {};
            //not a property from prototype chain
            // format value to be x.xx
            const formatValue = parseFloat(value).toFixed(2) * 30;
            console.log("formatValue=", formatValue);
            // format the HTML style attribute
            const height = formatValue + '%';
            console.log("height=",height);
            console.log("key=",key);

            newData.barStyle=height;
            newData.countryCode=key;
            newData.monetarySymbol='$';

            console.log("newData=", newData);  
            arrData.push(JSON.stringify(newData));          
        }
    }
    // Modify State
    console.log("arrData=", arrData);
    this.setState({
         rates:arrData
     });
}


