    {}

    i--;  // reposition counter to found item
    if (symbols[i] === this.baseCode)
    {
        comparator = symbols.splice(i,1);  // remmove elemment
    }
    console.log("symbols:", comparator);

    let fetchString='https://api.exchangeratesapi.io/2018-11-01?base=' + this.baseCode + "&symbols=" + symbols;

    // Make request
    fetch(fetchString)
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log(data);
           this.buildData(data.rates);
       });
}

