import React, { Component } from 'react';
import './App.css';


import Bar from './Components/Bar/Bar.js'
import Graph from './Components/Graph/Graph.js'

class App extends Component 
{

  state = {
    baseCode:"USD",
    rates:[],
  }


  // Add data during initial page load. Kind of likke constructor
  componentDidMount = () => {
    console.log("In did Mount and State:", this.state);
    this.getData();
  }

  render() 
  {

    return (
      <div>
        <h1 className="Title"> Currency</h1>
        <h2 className="BaseValue">Base Currency:</h2>
        <select basecode={this.baseCode} className="BaseCode" onChange={this.onBaseCodeChange}>
          <option value="USA">USA</option>
          <option value="AUD">AUD</option>
          <option value="SGD">SGD</option>
          <option value="EUR">EUR</option>
        </select>
        <Graph className="Graph">
          this.state.rates.map((code,idx)=>(     
          <Bar key={idx} className="Bar" style={{height:{code}}} title={code} symbol="$"/>
          )
         </Graph>
      </div>
    );
  }

  onBaseCodeChange = (ev) => {
    this.setState({
      baseCode: ev.target.value,
    });
  }

  getData()
  { 
    console.log("In GetData");


     fetch("https://api.exchangeratesapi.io/latest?base=USD&symbols=BGN,GBP,AUD,EUR")
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log("Data=", data);
           console.log("Rates=", data.rates);
           this.setState({
              rates:data.rates
       });
    });
  } 

}

export default App;
