import React, { Component } from 'react';
import './App.css';

import Graph from './Components/Graph/Graph.js'

function buildBar(rates)
{
    console.log("In buildBar");
    let bars = [];


    for(let key of Object.keys(rates))
    {
        const bar = {
              title:"",
              symbol:"",
              style:"$"
        };

        //Check for properties from prototype chain   
        if (rates.hasOwnProperty(key))
        {


            const value = rates[key];

            // format value to be x.xx
            const formatValue = parseFloat(value).toFixed(2) * 30;

            // Create a bar
            bar.title=key;
            bar.symbol='$';                
            bar.style = '{height:' + formatValue + '%}'; // format the JSX style height attribute          
            console.log("bar=", bar);  
            const tmp=JSON.sringify(bar);
            console.log("tmp.stringify=", tmp); 
            console.log("tmp.parse", JSON.parse(tmp));

            // add to array of bars
            // Or Origin.assign?
            bars.push(JSON.parse(tmp));
            console.log("bars=", bars);
        }
    }
    return bars;
}



class App extends Component 
{

  state = {
    baseCode:"USD",
    bars:[],
  }


  // Add data during initial page load. Kind of likke constructor
  componentDidMount = () => {
    console.log("In did Mount and State:", this.state);
    this.getData();
  }

  render() 
  {
    console.log("Apps.render");

    return (
      <div>
        <h1 className="Title"> Currency</h1>
        <h2 className="BaseValue">Base Currency:</h2>
        <select 
          basecode={this.baseCode} 
          className="BaseCode" 
          onChange={this.onBaseCodeChange}>
          <option value="USA">USA</option>
          <option value="AUD">AUD</option>
          <option value="SGD">SGD</option>
          <option value="EUR">EUR</option>
        </select>
        <Graph 
              className="Graph"
               bars={this.state.bars}        
         />
      </div>
    );
  }

  onBaseCodeChange = (ev) => {
    this.setState({
      baseCode: ev.target.value,
    });
  }

  getData()
  { 
    console.log("In GetData");


     fetch("https://api.exchangeratesapi.io/latest?base=USD&symbols=BGN,GBP,AUD,EUR")
     .then(response => response.json())
     .then ( (data) => {
           console.log("Got Data");
           console.log("Data=", data);
           console.log("Rates=", data.rates);
           const results = buildBar(data.rates);
           this.setState({
              bars:results
           });
       });
  } 
}

export default App;
